            AlternateGridBackground.Color = Color.Empty;

            AlternateGridBackground.Mode = BackgroundMode.Color;

            AlternateGridBackground.Bevel = false;

            GridLine.Color = Color.Empty;

            ZeroLine.Color = Color.Empty;

